    /* Dark Theme */
    @media (prefers-color-scheme: dark) {

        body,
        .pure-g,
        .pure-form legend {
            background: bg-d-1;
            color: fg-d-1; }

        /* Clickable Links */
        a:not(.navbar .index-link),
        a:active:not(.navbar .index-link),
        a:visited:not(.navbar .index-link),
        footer a,
        summary,
        a:not([data-id]) > .icon,
        a:link:not(.pure-button):not(.channel-owner) {
            color: link-d !important; }

        a:hover,
        footer a:hover,
        summary:hover,
        a:not([data-id]) > .icon:hover,
        a:link:not(.pure-button):not(.channel-owner):hover {
            color: link-d-h !important; }

        /* Multiple option Select Box */
        .pure-form {
            input[type="color"],
            input[type="date"],
            input[type="datetime-local"],
            input[type="datetime"],
            input[type="email"],
            input[type="month"],
            input[type="number"],
            input[type="password"],
            input[type="tel"],
            input[type="text"],
            input[type="time"],
