@-moz-document domain("redirect.invidious.io") {

    @media (prefers-color-scheme: light) {
        :root {
            --bg-primary: bg-l-3;
            --bg-secondary: bg-l-1;
            --bg-tertiary: bg-l-3;
            --fg-primary: fg-l-1;
            --fg-link: link-l;
            --fg-linkHover: link-l-h;
            --edge-table: tansparent; }

        ::selection,
        ::selection {
            background-color: fg-l-3;
            color: bg-l-1; } }

    @media (prefers-color-scheme: dark) {
        :root {
            --bg-primary: bg-d-1;
            --bg-secondary: bg-d-2;
            --bg-tertiary: bg-d-3;
            --fg-primary: fg-d-1;
            --fg-link: link-d;
            --fg-linkHover: link-d-h;
            --edge-table: tansparent; }

        ::selection,
        ::selection {
            background-color: fg-d-1;
            color: bg-d-3; } }

    :root {
        if select-color=="Frost" {
            --fg-header: nord-1;
            --fg-dim: nord-2; }

        if select-color=="Colourful" {
            --fg-header: red-n;
            --fg-dim: red-n; } }

    /* Hovering over links */
    a:hover {
        color: var(--fg-linkHover); }

    .script-warning {
        border: transparent;
        background: var(--fg-dim);
        color: var(--fg-primary); }

    /* Font */
    html,
    body,
    .pure-g [class*="pure-u"] {
        font-family: Roboto, Arial, sans-serif !important; } }
